import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import TextField from '@material-ui/core/TextField';
import { withStyles } from '@material-ui/core/styles';

// had to find a way of changing a react hook style to a class component style as my react hook form was not working properly found the solution here: https://github.com/mui-org/material-ui/issues/15820

const styles = (theme) => ({
    root: {
        '& .MuiTextField-root': {
          margin: theme.spacing(1),
          width: '99%',
        },
      },
  });

class AddMovieForm extends Component {

    state = {
        imdbID: "", 
        Title: "",
        Genre: "",
        Plot: "",
        Runtime: "",
        Year: "",
        Director: "",
        imdbRating: ""
    }

    // take in errorMessage array length?
    onSubmit = (formErrors) => {

      console.log(formErrors)

      // before adding movie need to check if all errors are gone
      this.props.addMovie(this.state)

      this.setState({
        imdbID: "", 
        Title: "",
        Genre: "",
        Plot: "",
        Runtime: "",
        Year: "",
        Director: "",
        imdbRating: ""
      })

      this.props.closeWithSubmit()
    }

    render()
    {
        const { classes } = this.props;

        // add each error to a list
        // onsubmit check the length of the error list
        // if the errors are === 0 then allow the onsubmit to happen

        let INVALID_MOVIE_ID = ""
        let INVALID_TITLE = ""
        let INVALID_GENRE = ""
        let INVALID_PLOT = ""
        let INVALID_RUNTIME = ""
        let INVALID_YEAR = ""
        let INVALID_DIRECTOR = ""
        let INVALID_IMDB_RATING = ""

        const formErrors = []

        // conditions for error messages

        if(!this.state.imdbID.match("[T][T][0-9]+"))
        {
          INVALID_MOVIE_ID += "Enter a valid Movie Id e.g TT012345"
          formErrors.push(INVALID_MOVIE_ID)
        }
        if(!this.state.Title.match("^[a-zA-Z0-9]{2,30}$"))
        {
          INVALID_TITLE += "Enter a valid Movie Title (range 2-30 characters)"
          formErrors.push(INVALID_TITLE)
        }
        if(!this.state.Genre.match("^[a-zA-Z0-9]{2,30}$"))
        {
          INVALID_GENRE += "Enter a valid Movie Genre (range 2-30 characters)"
          formErrors.push(INVALID_GENRE)
        }
        if(!this.state.Plot.match("^[a-zA-Z0-9]{2,200}$"))
        {
          INVALID_PLOT += "Enter a valid Movie Plot (range 2-200 characters)"
          formErrors.push(INVALID_PLOT)
        }
        if(!this.state.Runtime.match("^[0-9]{2,3} [m][i][n]$"))
        {
          INVALID_RUNTIME += "Enter a valid Movie Runtime e.g 90 min or 120 min"
          formErrors.push(INVALID_RUNTIME)
        }
        if(!this.state.Year.match("(?:(?:19|20)[0-9]{2})"))
        {
          INVALID_YEAR += "Enter a valid Movie Year (range 1900-2099)"
          formErrors.push(INVALID_YEAR)
        }
        if(this.state.Director.length < 2 || !this.state.Director.match("^[A-Za-z\\s]+$"))
        {
          INVALID_DIRECTOR += "Enter a valid Movie Director e.g Michael Jackson"
          formErrors.push(INVALID_DIRECTOR)
        }
        if(!this.state.imdbRating.match("^([0-9]|1[0])$"))
        {
          INVALID_IMDB_RATING += "Enter a valid IMDB Rating (1-10)"
          formErrors.push(INVALID_IMDB_RATING)
        }

        console.log(formErrors)

        const errorListItems = formErrors.map((formError, index) => <li key={index++}>{formError}</li>)

        return (
            <form className={classes.root} noValidate autoComplete="off" >
              <div>
                <TextField
                  required
                  name="movieId"
                  label="Movie Id"
                  placeholder="Enter Movie ID..."
                  variant="outlined"
                  value={this.state.imdbID}
                  onChange={e => this.setState({imdbID: e.target.value})}
                  helperText={INVALID_MOVIE_ID}
                  id="standard-error-helper-text"
                />

                <TextField
                  required
                  id="standard-required"
                  name="movieTitle"
                  label="Movie Title"
                  placeholder="Enter Movie Title..."
                  variant="outlined"
                  value={this.state.Title}
                  onChange={e => this.setState({Title: e.target.value})}
                  helperText={INVALID_TITLE}
                  id="standard-error-helper-text"
                />

                <TextField
                  required                
                  id="standard-required"
                  name="movieGenre"
                  label="Movie Genre"
                  placeholder="Enter Movie Genre..."
                  variant="outlined"
                  value={this.state.Genre}
                  onChange={e => this.setState({Genre: e.target.value})}
                  helperText={INVALID_GENRE}
                  id="standard-error-helper-text"
                />

                <TextField
                  required 
                  id="standard-required"
                  name="moviePlot"
                  label="Movie Plot"
                  placeholder="Enter Movie Plot..."
                  variant="outlined"
                  multiline={true}
                  fullWidth={true}
                  value={this.state.Plot}
                  onChange={e => this.setState({Plot: e.target.value})}
                  helperText={INVALID_PLOT}
                  id="standard-error-helper-text"
                />

                <TextField
                  required 
                  id="standard-required"
                  name="movieRuntime"
                  label="Movie Runtime"
                  placeholder="Enter Movie Runtime..."
                  variant="outlined"
                  value={this.state.Runtime}
                  onChange={e => this.setState({Runtime: e.target.value})}
                  helperText={INVALID_RUNTIME}
                  id="standard-error-helper-text"
                  />

                <TextField
                  required 
                  id="standard-required"
                  name="movieYear"
                  label="Movie Year"
                  placeholder="Enter Movie Year..."
                  variant="outlined"
                  value={this.state.Year}
                  onChange={e => this.setState({Year: e.target.value})}
                  helperText={INVALID_YEAR}
                  id="standard-error-helper-text"
                />

                <TextField
                  required 
                  id="standard-required"
                  name="movieDirector"
                  label="Movie Director"
                  placeholder="Enter Movie Year..."
                  variant="outlined"
                  value={this.state.Director}
                  onChange={e => this.setState({Director: e.target.value})}
                  helperText={INVALID_DIRECTOR}
                  id="standard-error-helper-text"
                />

                <TextField
                  required 
                  id="standard-required"
                  name="movieImdbRating"
                  label="IMDB Rating"
                  placeholder="Enter Movie Year..."
                  variant="outlined"
                  value={this.state.imdbRating}
                  onChange={e => this.setState({imdbRating: e.target.value})}
                  helperText={INVALID_IMDB_RATING}
                  id="standard-error-helper-text"
                />

                <ul>{errorListItems}</ul>

              <Button variant="primary" value="Submit" onClick={() => this.onSubmit(formErrors)} >
                Submit
              </Button>

              </div>
            </form>
          )
    }
}

export default withStyles(styles)(AddMovieForm);
